function sendmail(){var n=document.getElementById("send-btn");fetch("https://dwcre12gt6.execute-api.ap-northeast-2.amazonaws.com/run/mail_chobolife",{method:"post",headers:{"Content-Type":"application/json; charset=utf-8"},body:JSON.stringify({txt:document.getElementById("send-input").value})}).then(function(t){t.text().then(function(t){var e=JSON.parse(t).body;n.innerHTML=e?'<span class="btn btn--success">전송되었습니다. 소중한 의견 감사합니다!</span>':'<a href="javascript:recoverbtn()"><span class="btn btn--warning">내용을 입력해 주세요.</span></a>'})}),n.innerHTML='<span class="btn btn--warning">처리중입니다...</span>'}function recoverbtn(){document.getElementById("send-btn").innerHTML='<a href="javascript:sendmail()"><span class="btn btn--info">전송하기</span></a>'}
